                                    function pageRedirect() {
                                        var re = new RegExp(/^((?:(?:(?:\w[\.\-\+]?)*)\w)+)((?:(?:(?:\w[\.\-\+]?){0,62})\w)+)\.(\w{2,6})$/);
                                        var domain = $("#webredirect").val();

                                        if ( domain.indexOf('https://') > -1) {
                                            domain = domain.replace('https://','');
                                        } else if(domain.indexOf('http://') > -1) {
                                            domain = domain.replace('http://','');
                                        }

                                        if(domain.match(re)){ // voon code: check if webstore name is empty
                                            var go_to_url = 'https://' + domain + "/uc_admin";

                                            $(location).attr('href',go_to_url);

                                        } else { // voon code: else prompt error message
                                            $('.no_domain').remove();
                                            $('#login').after('<p class="no_domain text-danger" style="padding-top:5px">*Please input proper domain name*</p>');
                                            $('#webredirect').focus();

                                        }

                                    }

                                    function check_enter(e) {
                                        if(e.keyCode == 13){
                                            $("#login").click();
                                        }
                                    }

                                    //Display login modal
                                    jQuery(document).ready(function($) {
                                        var target = document.location.hash.replace("#", "");
                                        if (target.length) {
                                            if(target=="unicartlogin"){
                                                $('#unicartlogin').modal('show');
                                            }
                                        }
                                        else{
                                        }
                                    });

                                </script>
                                <br/>
                                <button id="login" type="button" onclick="pageRedirect()" class="btn btn-success btn-get">Next</button>
